<script>
import { store } from '../store';

import axios from 'axios';


export default {
    name: 'AppProject',
    data () {
        return {
            store,
            project: null,
            defaultImg: 'data:image/jpeg;base64,'
        }
    },
    methods: {
       getSingleProject: function () {
            axios
                .get(`http://127.0.0.1:8000/api/projects/${this.$route.params.slug}`)
                .then((response) => {
                    console.log(response);
                    return this.project = response.data;

                });
        }
    },
    created() {
        this.getSingleProject;
    }
}
</script>

<template>
   <div class="container">
        <div class="row">
            <div class="col">
                <div v-if="project">
                    <div class="project-img">
                        <img :src="project.img ?? defaultImg" :alt="project.title">
                    </div>
                    <h5>{{ project.title }}</h5>
                    <h6>
                        <i class="fa-solid fa-tag"></i> {{ project.type ? project.type.name : 'Non specificato' }}
                    </h6>
                    <div v-if="project.description">
                        <p class="card-text">
                            {{ project.description }}
                        </p>
                    </div>
                    <div v-if="project.technologies.length > 0">
                        <span class="badge text-bg-primary me-1" v-for="technology in project.technologies">
                            {{ technology.name }}
                        </span>
                    </div>
                </div>
            </div>
        </div>
    </div>

</template>

<style lang="scss" scoped>
</style>